mapper.select:
SELECT cs.STORE_SEQ ,cs.STORE_NAME ,cs.STORE_INTRO_SIMPLE,cs.STORE_STATE,cs.STORE_PHONE,
		cs.STORE_ADDRESS ,cs.STORE_ADDRESS_DETAIL ,ccm.CATEGORY_CODE, cc.CATEGORY_NAME 
FROM CON_STORE_LOCATION csl ,CON_STORE cs, CON_CATEGORY_MAIN ccm, CON_CATEGORY cc 
WHERE cs.STORE_SEQ =csl.STORE_SEQ
AND cs.STORE_SEQ =ccm.STORE_SEQ
AND cc.CATEGORY_CODE =ccm.CATEGORY_CODE 
AND STORE_LATITUDE BETWEEN #{swlat} AND #{nelat}
AND STORE_LONGITUDE BETWEEN #{swlng} AND #{nelng}
AND STORE_ADMIN_STATE ='Y'
AND ccm.CATEGORY_CODE =#{category_code}

SELECT cnp.STORE_SEQ ,cnp.STORE_PHOTO_SEQ ,cnp.STORE_PHOTO_STORED, cnp.rn, cnr.all_avg FROM ( SELECT cs.STORE_SEQ ,csp.STORE_PHOTO_SEQ ,csp.STORE_PHOTO_STORED, ROW_NUMBER ()over(PARTITION BY cs.STORE_SEQ ORDER BY csp.STORE_PHOTO_SEQ) AS rn FROM CON_STORE cs,CON_STORE_PHOTO csp WHERE cs.STORE_SEQ =csp.STORE_SEQ AND cs.STORE_SEQ IN
#{item.store_seq}
) cnp, (SELECT STORE_SEQ ,TRUNC((AVG(REPLY_CLEAN)+AVG(REPLY_SERVICE )+AVG(REPLY_PRICE))/3,1) AS all_avg FROM CON_REPLY cr WHERE cr.STORE_SEQ IN
#{item.store_seq}
GROUP BY cr.STORE_SEQ) cnr WHERE cnp.store_seq=cnr.store_seq AND rn=1

SELECT ccl.STORE_SEQ ,ccs.CATEGORY_NAME_SMALL FROM CON_CATEGORY_LIST ccl, CON_CATEGORY_SMALL ccs WHERE ccl.CATEGORY_CODE_SMALL =ccs.CATEGORY_CODE_SMALL AND ccl.STORE_SEQ IN
#{item.store_seq}
ORDER BY ccl.STORE_SEQ, ccs.CATEGORY_CODE_SMALL

SELECT * FROM CON_STORE_TIME cst WHERE cst.STORE_SEQ IN
#{item.store_seq}
ORDER BY cst.STORE_SEQ ,cst.STORE_TIME_SEQ
